mapper.select:
SELECT id
FROM checklist_runs
WHERE user_id      = #{userId}
  AND child_id     = #{childId}
  AND checklist_id = #{checklistId}
  AND `STATUS`     = 'DRAFT'
ORDER BY id DESC
LIMIT 1

SELECT COUNT(*)
FROM checklist_runs
WHERE id      = #{runId}
  AND user_id = #{userId}

SELECT `STATUS`
FROM checklist_runs
WHERE id      = #{runId}
  AND user_id = #{userId}
LIMIT 1
