mapper.select:
SELECT COUNT(*) > 0
FROM users
WHERE login_id = #{loginId}

SELECT COUNT(*) > 0
FROM users
WHERE email = #{email}

mapper.update:
UPDATE users
SET name  = #{name},
    phone = #{phone}
WHERE id = #{id}

UPDATE users
SET login_pw = #{loginPw}
WHERE id = #{id}

UPDATE users
SET profile_image = #{profileImage}
WHERE id = #{id}
